(function() {
  var assert, vows, xmlbuilder;

  vows = require('vows');

  assert = require('assert');

  xmlbuilder = require('../src/index.coffee');

  vows.describe('Text Processing').addBatch({
    'Nothing gets escaped': {
      topic: function() {
        return xmlbuilder.create('comment', {}, {}, {
          headless: true
        }).comment('<>\'"&\t\n\r');
      },
      'resulting XML': function(topic) {
        var xml;
        xml = '<comment><!-- <>\'"&\t\n\r --></comment>';
        return assert.strictEqual(topic.doc().toString(), xml);
      }
    }
  })["export"](module);

}).call(this);
