(function() {
  var assert, vows, xmlbuilder;

  vows = require('vows');

  assert = require('assert');

  xmlbuilder = require('../src/index.coffee');

  vows.describe('Creating XML').addBatch({
    'Headless': {
      topic: function() {
        return xmlbuilder.create('root', {}, {}, {
          headless: true
        }).ele('xmlbuilder', {
          'for': 'node-js'
        }).ele('repo', {
          'type': 'git'
        }, 'git://github.com/oozcitak/xmlbuilder-js.git');
      },
      'resulting XML': function(topic) {
        var xml;
        xml = '<root>' + '<xmlbuilder for="node-js">' + '<repo type="git">git://github.com/oozcitak/xmlbuilder-js.git</repo>' + '</xmlbuilder>' + '</root>';
        return assert.strictEqual(topic.end(), xml);
      }
    }
  })["export"](module);

}).call(this);
