(function() {
  var assert, vows, xmlbuilder;

  vows = require('vows');

  assert = require('assert');

  xmlbuilder = require('../src/index.coffee');

  vows.describe('Edit').addBatch({
    'Import': {
      topic: function() {
        var test13imported;
        test13imported = xmlbuilder.create('test13imported', {}, {}, {
          headless: true
        }).ele('node', 'imported');
        return xmlbuilder.create('test13', {}, {}, {
          headless: true
        }).importXMLBuilder(test13imported.doc());
      },
      'resulting XML': function(topic) {
        var xml;
        xml = '<test13><test13imported><node>imported</node></test13imported></test13>';
        return assert.strictEqual(topic.doc().toString(), xml);
      }
    }
  })["export"](module);

}).call(this);
