(function() {
  var assert, vows, xmlbuilder;

  vows = require('vows');

  assert = require('assert');

  xmlbuilder = require('../src/index.coffee');

  vows.describe('Editing').addBatch({
    'Remove item': {
      topic: function() {
        return xmlbuilder.create('test3', {}, {}, {
          headless: true
        }).e('node', 'first instance').u().e('node', 'second instance').remove().e('node', 'third instance');
      },
      'resulting XML': function(topic) {
        var xml;
        xml = '<test3><node>first instance</node><node>third instance</node></test3>';
        return assert.strictEqual(topic.end(), xml);
      }
    }
  })["export"](module);

}).call(this);
