(function() {
  var assert, vows, xmlbuilder;

  vows = require('vows');

  assert = require('assert');

  xmlbuilder = require('../src/index.coffee');

  vows.describe('Convert to String').addBatch({
    'end() method': {
      topic: function() {
        return xmlbuilder.create('test16', {
          'version': '1.1'
        }).ele('node').txt('test').end();
      },
      'resulting XML': function(topic) {
        var xml;
        xml = '<?xml version="1.1"?><test16><node>test</node></test16>';
        return assert.strictEqual(topic, xml);
      }
    }
  })["export"](module);

}).call(this);
